import Landing from "./pages/landing";
import SignUpPage from "./pages/signUpPage";
import { BrowserRouter, Route, Switch, useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import ReactLoading from "react-loading";
import React from "react";
import { io } from "socket.io-client";
const ENDPOINT = "https://localhost:3003";

function App() {    
  const Landing = React.lazy(()=>import("./pages/landing"));
  const SignUpPage = React.lazy(()=>import("./pages/signUpPage"));

  const socket = io(ENDPOINT, { secure: true, reconnect: true });
    socket.on("connect", function(){
        console.log("connected");
    });
    
    socket.on("disconnect", function(){
        console.log("disconnected");
    });
    
    socket.on("error", console.error);

  function chat() {
    return (
      <>
          <ul id="messages"></ul>
          <form id="form" action="">
          <input id="input" autoComplete="off" /><button>Send</button>
          </form>
      </>
    )
  }

  return (
    <>
    <BrowserRouter>
      <div className="App">
        <React.Suspense fallback={<div style={{backgroundColor: "#2E2C2C", height: "100vh", width: "100vw", display: "flex", justifyContent: "center", alignItems: "center"}}><ReactLoading type={"spinningBubbles"} color="grey" /></div>}>
        {chat()}
        </React.Suspense>
      </div> 
    </BrowserRouter>
    </>
  );
}

export default App;
